 <script>
function startTime() {
    var today = new Date();
    var h = today.getHours();
    var m = today.getMinutes();
    var s = today.getSeconds();
    m = checkTime(m);
    s = checkTime(s);
    document.getElementById('txt').innerHTML =" Latest Trending News:"+
    h + ":" + m + ":" + s;
    var t = setTimeout(startTime, 500);
}
function checkTime(i) {
    if (i < 10) {i = "0" + i};  // add zero in front of numbers < 10
    return i;
}
</script>
<body class="home blog one-right-sidebar hfeed" onload="startTime()">
 
 <?php 
$url = "http://newsapi.org/v1/articles?source=the-next-web&sortBy=latest&apiKey=3b433be2e4524dd9965cfa8c8052be11";
$ch = curl_init();
curl_setopt($ch, CURLOPT_URL, $url);
curl_setopt($ch, CURLOPT_RETURNTRANSFER, 1);
$a= curl_exec($ch);
curl_close($ch);
 
 $a=str_replace("'","%se",$a);
echo  "<input type='hidden' id='varaa' value='$a' />";
 
 
 ?>



<div id="txt"></div>
 <script>

var a=document.getElementById("varaa").value;
 
var obj = JSON.parse(a);
var l=obj.articles.length;
for(var x=0;x<=l;x++){
	var des=obj.articles[x].description;
	var title=obj.articles[x].title;
	var url=obj.articles[x].url;
	var image=obj.articles[x].urlToImage;
	title=title.replace("%se","'");
	des=des.replace("%se","'");
	document.write('<article id="post-2003" class="posts-list__item card post-2003 post type-post status-publish format-quote has-post-thumbnail hentry category-news tag-business post_format-post-format-quote has-thumb"><div class="post-list__item-content"><figure class="post-thumbnail post-thumbnail--ultra-small "><a href="'+url+'" class="post-thumbnail__link post-thumbnail--ultra-small"><img width="150" height="115" src="'+image+'" class="post-thumbnail__img wp-post-image" /></a>   </figure> <div class="post-body-right"><header class="entry-header"><h5 class="entry-title"><a href="'+url+'" rel="bookmark">'+title+'</a></h5> </header> <div class="entry-content">"'+des+'" </div> </div> </div> </article>');
	
}
 </script>
